import axios from 'axios'
import React, { useState,useEffect } from 'react'

const Read = () => {


    const [data,setData] = useState([]);

    function getData() {
        axios
        .get("https://63490da40b382d796c7c7605.mockapi.io/first-crud")
        .then((res) =>{
        console.log(res.data);
        setData(res.data);
        });
    }
    function handleDelete() {
        axios.delete("https://63490da40b382d796c7c7605.mockapi.io/first-crud/${")
    }

    useEffect(() =>{
        getData();
    },[]);

  return (
    <>
    <h2>Read Operation</h2>
    <table class="table">
    <thead>
      <tr>
        <th scope="col">#</th>
        <th scope="col">Name</th>
        <th scope="col">Email</th>
        <th scope="col">Action</th>
      </tr>
    </thead>
    {data.map((eachData) =>{
            return(
                <>
                <tbody>
                    <tr>
                        <th scope="row">{eachData.id}</th>       
                        <td>{eachData.name}</td>
                        <td>{eachData.email}</td>
                        <td><button className="btn-success">Edit</button>
                        </td>
                        <td>
                            <button className="btn-danger" onClick={()=>handleDelete(eachData.id)}>Delete</button>
                        </td>
                    </tr>

                    </tbody>


            </>
            );
        })

    }
  </table>
  </>
  );
};

export default Read
